// page init
jQuery(function() {
	initFixedScrollBlock();
	initTouchNav();
	initOpenClose();
	initCycleSlideShow();
	jQuery('input, textarea').placeholder();
});

// cycle scroll gallery init
function initCycleSlideShow() {
	var gallery = jQuery('.gallery');

	ResponsiveHelper.addRange({
		'..979': {
			on: function() {
				gallery.slick({
					slide: '.slide',
					prevArrow: '<a class="btn-prev" href="#"></a>',
					nextArrow: '<a class="btn-next" href="#"></a>',
					dots: false,
					infinite: true,
					centerMode: true,
					centerPadding: 0,
					speed: 500,
					slidesToShow: 1,
					slidesToScroll: 1,
					adaptiveHeight: false,
					draggable: true,
					swipe: true,
					easing: 'linear'
				});
			}
		},
		'980..': {
			on: function() {
				if (gallery.hasClass('slick-slider')) {
					gallery.slick('unslick');
				}
			}
		}
	});
}

// open-close init
function initOpenClose() {
	jQuery('#nav').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.drop',
		animSpeed: 400,
		effect: 'slide'
	});
}

// initialize fixed blocks on scroll
function initFixedScrollBlock() {
	jQuery('#wrapper').fixedScrollBlock({
		slideBlock: '#nav'
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

